export declare const themeYaml = "themes:\n  default:\n    name: \"Default Theme\"\n    variables:\n      color:\n        bg-primary: \"#0b0c10\"\n        bg-secondary: \"#1f2937\"\n        surface: \"#12141a\"\n        text: \"#e5e7eb\"\n        muted: \"#a1a1aa\"\n        primary: \"#22c55e\"\n        info: \"#06b6d4\"\n        warning: \"#f59e0b\"\n        danger: \"#ef4444\"\n        link: \"#22c55e\"\n        border: \"#1f2937\"\n      radius-card: 10\n      spacing:\n        xs: 6\n        sm: 10\n        md: 14\n        lg: 20\n      font-size: 16\n      poster-width: 154\n    selectors:\n      \".theme\":\n        background-color: \"var(color.bg-primary)\"\n        color: \"var(color.text)\"\n        font-family: \"Inter, system-ui, Avenir, Helvetica, Arial, sans-serif\"\n      button:\n        background-color: \"#2563eb\"\n        color: \"#ffffff\"\n        border-radius: 6\n        padding: \"8px 12px\"\n      \".border\":\n        border: \"1px solid var(color.border)\"\n      \"h1, h2, h3, h4, h5, h6\":\n        margin-top: \"1.5rem\"\n        margin-bottom: \"0.5rem\"\n        line-height: \"1.2\"\n      h1:\n        font-size: \"2.2rem\"\n        padding-bottom: \"0.35rem\"\n      h2:\n        font-size: \"1.75rem\"\n        padding-bottom: \"0.25rem\"\n      p:\n        margin-bottom: \"1rem\"\n      \"ul, ol\":\n        margin-left: \"1.25rem\"\n        margin-bottom: \"1rem\"\n      blockquote:\n        border-left: \"4px solid var(color.primary)\"\n        padding-left: \"1rem\"\n      code:\n        background-color: \"var(color.bg-primary)\"\n        border: \"1px solid var(color.border)\"\n        border-radius: \"0.5rem\"\n        padding: \"0.2rem 0.4rem\"\n        font-family: \"'JetBrains Mono', 'Fira Code', Consolas, monospace\"\n      pre:\n        background-color: \"var(color.bg-primary)\"\n        border: \"1px solid var(color.border)\"\n        border-radius: \"0.75rem\"\n        padding: \"1rem\"\n        overflow-x: \"auto\"\n      table:\n        width: \"100%\"\n        border-collapse: \"collapse\"\n        margin-bottom: \"1rem\"\n      \"td, th\":\n        border: \"1px solid var(color.border)\"\n        padding: \"0.75rem\"\n      th:\n        background-color: \"var(color.surface)\"\n        font-weight: 600\n      hr:\n        border: 0\n        height: \"1px\"\n        background-color: \"1px solid var(color.border)\"\n        margin: \"2rem 0\"\n    breakpoints:\n      xs: \"480px\"\n      sm: \"640px\"\n      md: \"768px\"\n      lg: \"1024px\"\n      xl: \"1280px\"\n  dark:\n    name: \"Dark Theme\"\n    inherits: default\n    variables:\n      color:\n        bg-primary: \"#05060a\"\n        surface: \"#0b0f1a\"\n        text: \"#d1d5db\"\n        muted: \"#9ca3af\"\n        primary: \"#38bdf8\"\n        info: \"#a78bfa\"\n        warning: \"#f59e0b\"\n        danger: \"#ef4444\"\n        link: \"#38bdf8\"\n        border: \"#30363d\"\n\n  light:\n    name: \"Light Theme\"\n    inherits: default\ndefault_theme: default\n";
